    #[test]
    fn full_finality_state_to_partial_finality_state_v1() {
        let header = HeaderV1::decode_as::<Bincode>(&"".parse::<Bytes>().unwrap()).unwrap();

        dbg!(&header);

        let ClientState::V1(client_state) = serde_json::from_str::<ClientState>(r#"{"v1":{"chain_id":"21000000","frozen_height":"0","ibc_contract_address":"0xee4ea8d358473f0fcebf0329feed95d56e8c04d7","l1_client_id":1,"l1_contract_address":"0x828c71bc1d7a34f32ffa624240633b6b7272c3d6","latest_height":400311}}"#).unwrap() else { panic!() };

        v1::verify_header(
            &client_state,
            &header,
            hex!("df342885026f59355558d4bc2fe6f9b4e66e6edacc68fba65e1a694a05400e59").into(),
        )
        .unwrap();
    }

